// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IBridgeState.sol";

contract Bridge is Pausable, Ownable {
    // Mapping of  processed root chain transactions by root chain ID and txn hash. We follow official EVM Chain Id Standards.
    // e.g:
    // 1 is Ethereum Mainnet
    // 4 is Rinkeby
    mapping(uint32 => mapping(string => bool)) public processedTxnIds;

    uint8 public signatureThreshold = 2;

    // This variable ensures the bridge contract can check its own ID is equal to destinationChainId inside release tokens logic.
    uint32 public thisChainId; // Ethereum Mainnet Chain is Id'd 1;

    address public stateContract; // This address is basically of BridgeState which contains all the verifying methods and governance methods of the bridge.

    // To make things ready-to-run. Deployer provides 3 Soldier addresses in constructor. Deployer is automatically bridge admin, owner, and pauser role.
    constructor(uint32 chainId, address _stateAddress) {
        thisChainId = chainId;
        stateContract = _stateAddress;
    }

    // Events to monitor the bridge.

    event Lock(
        address fromAddr,
        string toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        address tokenAddr,
        uint256 amount,
        uint8 signatureThreshold,
        uint256 timestamp
    );

    event BridgeSuccess(
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        address tokenAddr,
        string rootTxnId,
        uint256 amount,
        uint256 timestamp
    );

    // Multisig variables.
    function changeSignatureThreshold(uint8 newThreshold) public onlyOwner {
        if (newThreshold >= 2) {
            signatureThreshold = newThreshold;
        }
    }

    // Release Tokens.
    function releaseTokens(
        address tokenAddr,
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        string memory rootTxnId,
        uint256 amount,
        bytes[] memory signatures
    ) public whenNotPaused returns (bool) {
        require(
            IBridgeState(stateContract).isTokenValid(tokenAddr),
            "Trying to release non existent token."
        );
        // Require that you have enough signature to run the validation scheme.
        require(
            signatures.length >= signatureThreshold,
            "Length of signatures array is smaller than the required threshold"
        );

        // Reconstruct the hash that the soldiers signed using the function parameters.
        bytes32 reconstructedHash = reconstructMessageHash(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount
        );
        for (uint256 i = 0; i < signatureThreshold; i++) {
            address verifiedAddress = recoverSigner(
                reconstructedHash,
                signatures[i]
            );
            bool checkSoldier = IBridgeState(stateContract).isSoldier(
                verifiedAddress
            );
            require(checkSoldier, "Invalid signature");
        }

        // Check that this transaction has never been processed before.
        require(
            processedTxnIds[rootChainId][rootTxnId] != true,
            "Transaction ID already processed"
        );
        processedTxnIds[rootChainId][rootTxnId] = true;

        // Emit success event.
        require(
            destinationChainId == thisChainId,
            "Destination Chain Id not equal to the Blockchain hosting this function call"
        );

        // Transfer.
        IERC20(tokenAddr).transfer(toAddr, amount);

        emit BridgeSuccess(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount,
            block.timestamp
        );
        return true;
    }

    // Lock tokens inside the bridge.
    // Root chain will automatically be the ID of the chain where this contract is deployed to.
    // tokenAddr will be checked against Whitelist.
    function lockTokens(
        string memory toAddr,
        uint32 destinationChainId,
        address tokenAddr,
        uint256 amount
    ) public whenNotPaused returns (bool) {
        require(
            IBridgeState(stateContract).isTokenValid(tokenAddr),
            "Trying to bridge non existent token."
        );
        IERC20(tokenAddr).transferFrom(msg.sender, address(this), amount);
        // Lock event accounts with rootChainId = thisChainId.
        emit Lock(
            msg.sender,
            toAddr,
            thisChainId,
            destinationChainId,
            tokenAddr,
            amount,
            signatureThreshold,
            block.timestamp
        );
        return true;
    }

    // Utils.
    function prefixed(bytes32 message) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", message)
            );
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        public
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            uint8,
            bytes32,
            bytes32
        )
    {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix.
            r := mload(add(sig, 32))
            // second 32 bytes.
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes).
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function reconstructMessageHash(
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        address tokenAddr,
        string memory rootTxnId,
        uint256 amount
    ) public pure returns (bytes32) {
        return
            prefixed(
                keccak256(
                    abi.encodePacked(
                        toAddr,
                        rootChainId,
                        destinationChainId,
                        tokenAddr,
                        rootTxnId,
                        amount
                    )
                )
            );
    }
}
