// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./BridgeGovernance.sol";

contract Bridge is Pausable, BridgeGovernance {
    // solves too many arguments error
    struct EthChainInfo {
        uint32 chainId;
        address tokenId;
        uint256 amount;
        address addressId;
    }

    struct ExternalChainInfo {
        uint32 chainId;
        string tokenId;
        uint256 amount;
        string addressId;
    }
    // Events to monitor the bridge.
    // This event is emitted when user wants to lock existing ERC20 token to a foreign chain.
    // Tokens will be locked into the bridge smart contract.
    event Lock(
        address feeTokenAddr,
        uint256 feeAmount,
        EthChainInfo sourceChainData,
        ExternalChainInfo destinationChainData,
        string note,
        uint256 timestamp
    );
    // This event is emitted when a user successfully bridged tokens on this chain.
    event BridgeSuccess(
        string sourceTransactionId,
        ExternalChainInfo sourceChainData,
        EthChainInfo destinationChainData,
        string note,
        uint256 timestamp
    );

    /**
     * @dev This function is called to release tokens that were previously locked in this smart contract.
     * This is the function called in the case of a bridge request with this chain as destination.
     */
    function releaseTokens(
        uint64 maxReleaseRound,
        string memory sourceTransactionId,
        ExternalChainInfo memory sourceChainData,
        EthChainInfo memory destinationChainData,
        string memory note,
        bytes[] memory signatures
    ) public whenNotPaused returns (bool) {
        require(
            tokens[destinationChainData.tokenId],
            "Trying to release non existent token."
        );
        require(
            block.number < maxReleaseRound,
            "Transaction must be released within defined period"
        );
        require(
            destinationChainData.chainId == thisChainId,
            "Destination Chain Id not equal to the Blockchain hosting this function call"
        );
        // Check that this transaction has never been processed before.
        Transaction storage transaction = transactions[sourceChainData.chainId][
            sourceTransactionId
        ];
        require(
            transaction.processed != true,
            "Transaction ID already processed"
        );
        transaction.processed = true;

        // Reconstruct the hash that the soldiers signed using the function parameters.
        bytes32 reconstructedHash = reconstructMessageHash(
            maxReleaseRound,
            sourceTransactionId,
            sourceChainData,
            destinationChainData,
            note
        );
        validateSignatures(reconstructedHash, signatures, transaction.signers);

        if (destinationChainData.tokenId == address(0)) {
            payable(destinationChainData.addressId).transfer(
                destinationChainData.amount
            );
        } else {
            IERC20(destinationChainData.tokenId).transfer(
                destinationChainData.addressId,
                destinationChainData.amount
            );
        }

        emit BridgeSuccess(
            sourceTransactionId,
            sourceChainData,
            destinationChainData,
            note,
            block.timestamp
        );
        return true;
    }

    /**
     * @dev This function lock tokens inside the bridge smart contract. This is used for bridge/swap requests originated on this chain and with destination chain represented by destinationChain.
     */
    function lockTokens(
        address feeTokenAddr, // token to pay fee
        uint256 feeAmount, // fee amount to be locked
        address rootTokenAddr, // token at this network
        uint256 rootAmount, // amount to be locked at this network
        ExternalChainInfo memory destinationChainData,
        string memory note
    ) public payable whenNotPaused returns (bool) {
        require(bytes(note).length <= 50, "Note must be shorter than 50 bytes");
        require(tokens[rootTokenAddr], "Trying to bridge non existent token.");

        // Four cases:
        // 1 - Token used to pay fee is different from token to bridge && fee > 0
        if (
            rootTokenAddr != feeTokenAddr &&
            feeAmount > 0 &&
            feeTokenAddr != address(0)
        ) {
            require(
                tokens[feeTokenAddr],
                "Trying to bridge non existent token."
            );
            IERC20(feeTokenAddr).transferFrom(
                msg.sender,
                address(this),
                feeAmount
            );
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else if (rootTokenAddr == feeTokenAddr && feeAmount > 0) {
            // 2 - Token used to pay fee is the same as token to bridge && fee > 0
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount + feeAmount
            );
        } else if (
            msg.value == feeAmount &&
            feeTokenAddr == address(0) &&
            msg.value > 0
        ) {
            // 3 - Fee is paid in native currency (e.g. eth or matic)
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else if (feeAmount == 0) {
            // 4 - Fee is 0. Will be paid on destination network or denied by soldier network
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else {
            require(
                false,
                "Problem occurred while trying to select a transfer case"
            );
        }

        // Lock event accounts with rootChainId = thisChainId.
        emit Lock(
            feeTokenAddr,
            feeAmount,
            EthChainInfo(thisChainId, rootTokenAddr, rootAmount, msg.sender),
            destinationChainData,
            note,
            block.timestamp
        );
        return true;
    }

    /**
     * @dev This function lock native eth (or native chain token) inside the bridge smart contract.
     */
    function lockNativeCurrency(
        address feeTokenAddr, // token to pay fee
        uint256 feeAmount, // fee amount to be locked
        uint256 rootAmount, // amount to be locked at this network
        ExternalChainInfo memory destinationChainData,
        string memory note
    ) public payable whenNotPaused returns (bool) {
        require(bytes(note).length <= 50, "Note must be shorter than 50 bytes");
        address fakeTokenAddrForNativeCurrency = address(0);
        // Three cases:
        if (msg.value == rootAmount + feeAmount) {
            // 1 - User pays in native currency the fee.
        } else if (
            msg.value == rootAmount &&
            feeAmount > 0 &&
            feeTokenAddr != address(0)
        ) {
            // 2 - Token used to pay fee is a different token
            require(
                tokens[feeTokenAddr],
                "Trying to pay fee with non existent token."
            );
            IERC20(feeTokenAddr).transferFrom(
                msg.sender,
                address(this),
                feeAmount
            );
        } else if (msg.value == rootAmount && feeAmount == 0) {
            // 3 - Fee is 0. Will be paid on destination network or denied by soldier network
        } else {
            require(false, "There was a problem in the fee ifs conditions");
        }

        // Lock event accounts with rootChainId = thisChainId.
        emit Lock(
            feeTokenAddr,
            feeAmount,
            EthChainInfo(
                thisChainId,
                fakeTokenAddrForNativeCurrency,
                rootAmount,
                msg.sender
            ),
            destinationChainData,
            note,
            block.timestamp
        );
        return true;
    }

    /**
     * @dev Used to deposit money directly inside the bridge.
     */
    function deposit() public payable {}

    /**
     * @dev used to reconstruct a hash digest starting from some parameters.
     */
    function reconstructMessageHash(
        uint64 maxReleaseRound,
        string memory sourceTransactionId,
        ExternalChainInfo memory sourceChainData,
        EthChainInfo memory destinationChainData,
        string memory note
    ) public pure returns (bytes32) {
        uint256 sourceHash = uint256(
            keccak256(
                abi.encodePacked(
                    sourceChainData.addressId,
                    sourceChainData.amount,
                    sourceChainData.chainId,
                    sourceChainData.tokenId
                )
            )
        );
        uint256 destinationHash = uint256(
            keccak256(
                abi.encodePacked(
                    destinationChainData.addressId,
                    destinationChainData.amount,
                    destinationChainData.chainId,
                    destinationChainData.tokenId
                )
            )
        );
        return
            prefixed(
                keccak256(
                    abi.encodePacked(
                        maxReleaseRound,
                        sourceTransactionId,
                        sourceHash,
                        destinationHash,
                        note
                    )
                )
            );
    }
}
