// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Upgrade.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "./BridgeSignatureValidator.sol";

contract BridgeGovernance is
    BridgeSignatureValidator,
    ERC1967Upgrade,
    Initializable
{
    event LogTokenAdded(address tokenAddress);
    event LogTokenRemoved(address tokenAddress);
    event LogSoldierAdded(address soldierAddress);
    event LogSoldierRemoved(address soldierAddress);
    event LogThresholdChanged(uint8 threshold);

    function initialize(
        address[] memory initialSoldiers,
        address[] memory initialTokens,
        uint8 threshold,
        uint32 chainId
    ) public initializer {
        require(initialSoldiers.length >= threshold, "Not enough soldiers");
        for (uint8 i = 0; i < initialSoldiers.length; i++) {
            require(!soldiers[initialSoldiers[i]], "Soldier already added");
            soldiers[initialSoldiers[i]] = true;
            signers[nonce][msg.sender] = true;
        }
        soldierCount = initialSoldiers.length;

        for (uint8 i = 0; i < initialTokens.length; i++) {
            require(!tokens[initialTokens[i]], "Token already added");
            tokens[initialTokens[i]] = true;
            signers[nonce][msg.sender] = true;
        }
        nonce++;

        signaturesThreshold = threshold;
        thisChainId = chainId;
    }

    modifier onlySoldier() {
        require(soldiers[msg.sender], "caller is not one of the soldiers");
        _;
    }

    modifier validNonce(uint256 requestNonce) {
        require(requestNonce == nonce, "Invalid nonce");
        _;
        nonce++;
    }

    function addToken(
        address tokenAddress,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        require(!tokens[tokenAddress], "Token already active");
        validateSignatures(
            reconstructMessage(tokenAddress, requestNonce),
            signatures,
            signers[requestNonce]
        );
        tokens[tokenAddress] = true;
        emit LogTokenAdded(tokenAddress);
    }

    function removeToken(
        address tokenAddress,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        require(tokens[tokenAddress], "Token already not active");
        validateSignatures(
            reconstructMessage(tokenAddress, requestNonce),
            signatures,
            signers[requestNonce]
        );
        delete tokens[tokenAddress];
        emit LogTokenRemoved(tokenAddress);
    }

    function changeSignatureThreshold(
        uint8 threshold,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        validateSignatures(
            reconstructMessageUint8(threshold, requestNonce),
            signatures,
            signers[requestNonce]
        );
        signaturesThreshold = threshold;
        emit LogThresholdChanged(threshold);
    }

    function addSoldier(
        address soldierAddress,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        require(!soldiers[soldierAddress], "Soldier already active");
        validateSignatures(
            reconstructMessage(soldierAddress, requestNonce),
            signatures,
            signers[requestNonce]
        );
        soldiers[soldierAddress] = true;
        soldierCount++;
        emit LogSoldierAdded(soldierAddress);
    }

    function removeSoldier(
        address soldierAddress,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        require(soldierCount > signaturesThreshold, "Not enough soldiers");
        require(soldiers[soldierAddress], "Not a solider");
        validateSignatures(
            reconstructMessage(soldierAddress, requestNonce),
            signatures,
            signers[requestNonce]
        );
        delete soldiers[soldierAddress];
        soldierCount--;
        emit LogSoldierRemoved(soldierAddress);
    }

    function upgrade(
        address newImplementation,
        uint256 requestNonce,
        bytes[] memory signatures
    ) public onlySoldier validNonce(requestNonce) {
        validateSignatures(
            reconstructMessage(newImplementation, requestNonce),
            signatures,
            signers[requestNonce]
        );
        _upgradeTo(newImplementation);
    }

    function getNonce() public view returns (uint256) {
        return nonce;
    }

    function reconstructMessage(address addressToAdd, uint256 requestNonce)
        private
        pure
        returns (bytes32)
    {
        return
            prefixed(keccak256(abi.encodePacked(addressToAdd, requestNonce)));
    }

    function reconstructMessageUint8(uint8 number, uint256 requestNonce)
        private
        pure
        returns (bytes32)
    {
        return prefixed(keccak256(abi.encodePacked(number, requestNonce)));
    }
}
