// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract BridgeState {
    struct Transaction {
        bool processed;
        mapping(address => bool) signers;
    }

    uint256 internal soldierCount;
    uint256 internal nonce = 0;
    uint8 internal signaturesThreshold;

    mapping(address => bool) public soldiers;
    mapping(address => bool) public tokens;
    // this mapping is used for signature deduplication per nonce
    mapping(uint256 => mapping(address => bool)) internal signers;

    // mapping of processed root chain transactions by root chain ID and txn hash.
    // follows official EVM Chain Id Standards
    mapping(uint32 => mapping(string => Transaction)) public transactions;
    uint32 public thisChainId;
}
