// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Bridge is Pausable, Ownable {
    // Mapping of  processed root chain transactions by root chain ID and txn hash.
    // e.g:
    // 0 is Ethereum
    // 1 is Algorand
    // 2 is Polygon, etc.
    mapping(uint8 => mapping(string => bool)) public processedTxnIds;

    // Defining roles.
    // There can be an owner, an admin, and a soldier.
    // The owner can transfer ownership of contract, choose new admins/soldiers and revoke them.
    // Admins can add and revoke soldiers.
    mapping(address => bool) public isSoldier;
    mapping(address => bool) public isBridgeAdmin;

    // Mapping to link every asset from other chains to its EVM-based pair.
    mapping(uint256 => address) public assetIdToTokenAddress;
    mapping(address => bool) public tokenWhitelist;

    uint8 public signatureThreshold = 2;
    uint8 public thisChainId = 0; // Ethereum Chain is Id'd 0;

    // To make things ready-to-run. Deployer provides 3 Soldier addresses in constructor. Deployer is automatically bridge admin, owner, and pauser role.
    constructor(
        address[3] memory initialSoldiers,
        uint8 chainId,
        address[2] memory tokens
    ) {
        isBridgeAdmin[msg.sender] = true;
        isSoldier[initialSoldiers[0]] = true;
        isSoldier[initialSoldiers[1]] = true;
        isSoldier[initialSoldiers[2]] = true;
        thisChainId = chainId;
        tokenWhitelist[tokens[0]] = true;
        tokenWhitelist[tokens[1]] = true;
    }

    // Events to monitor the bridge.

    event Lock(
        address fromAddr,
        string toAddr,
        uint8 rootChainId,
        uint8 destinationChainId,
        address tokenAddr,
        uint256 amount,
        uint8 signatureThreshold,
        uint256 timestamp
    );

    event BridgeSuccess(
        address toAddr,
        uint8 rootChainId,
        uint8 destinationChainId,
        address tokenAddr,
        string rootTxnId,
        uint256 amount,
        uint256 timestamp
    );

    // Role assignment and modifiers.
    modifier onlySoldier() {
        require(isSoldier[msg.sender], "Not a part of the Soldier whitelist");
        _;
    }
    modifier onlyAdmin() {
        require(
            isBridgeAdmin[msg.sender],
            "Not a part of the Bridge Admin whitelist"
        );
        _;
    }

    function addAdmin(address adminAddr) external onlyOwner {
        isBridgeAdmin[adminAddr] = true;
    }

    function removeAdmin(address adminAddr) external onlyOwner {
        isBridgeAdmin[adminAddr] = false;
    }

    function renounceAdmin() external onlyAdmin {
        isBridgeAdmin[msg.sender] = false;
    }

    function addSoldier(address soldierAddr) external onlyAdmin {
        isSoldier[soldierAddr] = true;
    }

    function removeSoldier(address soldierAddr) external onlyAdmin {
        isSoldier[soldierAddr] = false;
    }

    function renounceSoldier() external onlySoldier {
        isSoldier[msg.sender] = false;
    }

    // Multisig variables.
    function changeSignatureThreshold(uint8 newThreshold) public onlyOwner {
        signatureThreshold = newThreshold;
    }

    // Token whitelist & mappings manipulation.
    function addTokenToBridge(address tokenAddr) public onlyAdmin {
        tokenWhitelist[tokenAddr] = true;
    }

    function removeTokenFromBridge(address tokenAddr) public onlyAdmin {
        tokenWhitelist[tokenAddr] = false;
    }

    function addAssetToBridgeMapping(uint256 assetId, address tokenAddr)
        public
        onlyAdmin
    {
        assetIdToTokenAddress[assetId] = tokenAddr;
    }

    function removeAssetFromBridgeMapping(uint256 assetId) public onlyAdmin {
        assetIdToTokenAddress[assetId] = address(0);
    }

    // Bridging mechanisms, modifier whenNotPaused function is applied. Pauser role can pause locking and releasing in case of problem.

    // Release Tokens.
    function releaseTokens(
        address toAddr,
        uint8 rootChainId,
        uint8 destinationChainId,
        address tokenAddr,
        string memory rootTxnId,
        uint256 amount,
        bytes[] memory signatures
    ) public whenNotPaused returns (bool) {
        // Require that you have enough signature to run the validation scheme.
        require(
            signatures.length >= signatureThreshold,
            "Length of signatures array is smaller than the required threshold"
        );

        // Reconstruct the hash that the soldiers signed using the function parameters.
        bytes32 reconstructedHash = reconstructMessageHash(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount
        );
        for (uint256 i = 0; i < signatureThreshold; i++) {
            require(
                isSoldier[recoverSigner(reconstructedHash, signatures[i])],
                "Invalid signature"
            );
        }

        // Grabbing the ERC20 Token Address from the Foreign Chains Asset Mapping.
        require(tokenWhitelist[tokenAddr], "Invalid token");

        // Check that this transaction has never been processed before.
        require(
            processedTxnIds[rootChainId][rootTxnId] != true,
            "Transaction ID already processed"
        );
        processedTxnIds[rootChainId][rootTxnId] = true;

        // Transfer.
        IERC20(tokenAddr).transfer(toAddr, amount);

        // Emit success event.
        require(
            destinationChainId == thisChainId,
            "Destination Chain Id not equal to the Blockchain hosting this function call"
        ); // Ethereum is ID: 0.
        emit BridgeSuccess(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount,
            block.timestamp
        );
        return true;
    }

    // Lock tokens inside the bridge.
    // Root chain will automatically be the ID of the chain where this contract is deployed to.
    // tokenAddr will be checked against Whitelist.
    function lockTokens(
        string memory toAddr,
        uint8 destinationChainId,
        address tokenAddr,
        uint256 amount
    ) public whenNotPaused returns (bool) {
        require(tokenWhitelist[tokenAddr], "Token not accepted by our bridge");
        IERC20(tokenAddr).transferFrom(msg.sender, address(this), amount);
        // Lock event accounts for rootChainId. Here, we set it to 0 (Ethereum).
        uint8 rootChainId = 0; // Ethereum ID.
        emit Lock(
            msg.sender,
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            amount,
            signatureThreshold,
            block.timestamp
        );
        return true;
    }

    // Utils.
    function prefixed(bytes32 message) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", message)
            );
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        public
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            uint8,
            bytes32,
            bytes32
        )
    {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix.
            r := mload(add(sig, 32))
            // second 32 bytes.
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes).
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function reconstructMessageHash(
        address toAddr,
        uint8 rootChainId,
        uint8 destinationChainId,
        address tokenAddr,
        string memory rootTxnId,
        uint256 amount
    ) public pure returns (bytes32) {
        return
            prefixed(
                keccak256(
                    abi.encodePacked(
                        toAddr,
                        rootChainId,
                        destinationChainId,
                        tokenAddr,
                        rootTxnId,
                        amount
                    )
                )
            );
    }
}
